import { mount } from '@vue/test-utils';
import { defineComponent } from 'vue';
import DxSelectBox from '../../select-box';
import DxTextBox from '../../text-box';
jest.setTimeout(1000);
beforeEach(function () {
    jest.clearAllMocks();
});
describe('template rendering', function () {
    it('field template rendered', function () {
        var vm = defineComponent({
            template: "<dx-select-box :data-source=\"dataSource\" field-template=\"field\" id=\"component\">\n                    <template #field=\"\">\n                        <dx-text-box value=\"text\" />\n                    </template>\n                </dx-select-box >",
            data: function () {
                return {
                    dataSource: [{ ID: 1 }],
                };
            },
            components: {
                DxSelectBox: DxSelectBox,
                DxTextBox: DxTextBox,
            },
        });
        var wrapper = mount(vm);
        expect(wrapper.getComponent('#component').vm.$el.children).toHaveLength(1);
    });
});
