import { Plugin } from 'vite';

declare type ProxyItem = [prefix: string, target: string];
declare type ProxyList = ProxyItem[];

interface Options {
    /**
     * Proxy list, config this will override the .env proxy configuration
     * @default []
     */
    proxy?: ProxyList;
    /**
     * Output path
     * @default 'web.config'
     */
    output?: string | false;
}
declare function WebConfig(options?: Options): Plugin;

export { Options, WebConfig as default };
